import React, { useState } from 'react';

const Home = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [city, setCity] = useState('');

  const cities = ['london', 'newyork', 'tokyo', 'sydney'];

  const fetchWeather = async (cityName) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`http://localhost:8080/weather/${cityName}`);
      if (!response.ok) throw new Error('Weather data not found');
      const data = await response.json();
      setWeatherData(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCustomCity = async (e) => {
    e.preventDefault();
    if (city.trim()) {
      await fetchWeather(city.toLowerCase());
    }
  };

  return (
    <>
      <div className="hero-section">
        <div className="hero-tagline">
          We bring you precise forecasts, so you never get caught unprepared by rain, shine, or storm.
        </div>
      </div>
      
      <div className="weather-container">
      <h1>Weather Info Service</h1>
      
      <div className="city-buttons">
        {cities.map(cityName => (
          <button 
            key={cityName}
            onClick={() => fetchWeather(cityName)}
            className="city-btn"
          >
            {cityName.charAt(0).toUpperCase() + cityName.slice(1)}
          </button>
        ))}
      </div>

      <form onSubmit={handleCustomCity} className="custom-city">
        <input
          type="text"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          placeholder="Enter city name"
          className="city-input"
        />
        <button type="submit" className="search-btn">Search</button>
      </form>

      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error"> {error}</div>}
      
      {weatherData && (
        <div className="weather-card">
          <h2>{weatherData.city}</h2>
          <div className="weather-info">
            <div className="temp">{weatherData.temperature}Â°C</div>
            <div className="condition">{weatherData.condition}</div>
            <div className="humidity">Humidity: {weatherData.humidity}%</div>
            <div className="timestamp">
              Updated: {new Date(weatherData.timestamp).toLocaleString()}
            </div>
          </div>
        </div>
      )}
      </div>
    </>
  );
};

export default Home;
