import React, { useState } from 'react';
import heroImage from '../images/hg.jpg';
import marineImage from '../images/bg1.jpg';

const Home = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [marineData, setMarineData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [city, setCity] = useState('');
  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');
  const [isMarineMode, setIsMarineMode] = useState(false);

  const cities = ['london', 'newyork', 'tokyo', 'sydney'];

  const generateForecastData = (baseData) => {
    const forecast = [];
    for (let i = 1; i <= 4; i++) {
      forecast.push({
        temperature: baseData.temperature + Math.floor(Math.random() * 10 - 5),
        condition: ['Sunny', 'Cloudy', 'Rainy', 'Partly Cloudy'][Math.floor(Math.random() * 4)],
        humidity: baseData.humidity + Math.floor(Math.random() * 20 - 10),
        dt: Date.now() / 1000 + (i * 24 * 60 * 60)
      });
    }
    return forecast;
  };

  const generateMarineForecastData = (baseData) => {
    const forecast = [];
    for (let i = 1; i <= 2; i++) {
      forecast.push({
        waveHeight: baseData.waveHeight + (Math.random() * 0.5 - 0.25),
        windSpeed: baseData.windSpeed + (Math.random() * 4 - 2),
        windDirection: baseData.windDirection + (Math.random() * 20 - 10),
        waterTemperature: baseData.waterTemperature + (Math.random() * 2 - 1),
        dt: Date.now() / 1000 + (i * 24 * 60 * 60)
      });
    }
    return forecast;
  };

  const fetchWeather = async (cityName) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`http://localhost:8080/weather/${cityName}`);
      if (!response.ok) throw new Error('Weather data not found');
      const data = await response.json();
      
      data.daily = generateForecastData(data);
      
      setWeatherData(data);
      setMarineData(null);
    } catch (err) {
      setError(`Failed to fetch: ${err.message}`);
      console.error('Fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchMarineWeather = async (lat, lng) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`http://localhost:8080/weather/marine?lat=${lat}&lng=${lng}`);
      if (!response.ok) throw new Error('Marine weather data not found');
      const data = await response.json();
      
      data.daily = generateMarineForecastData(data);
      
      setMarineData(data);
      setWeatherData(null);
    } catch (err) {
      setError(`Failed to fetch: ${err.message}`);
      console.error('Fetch error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleCustomCity = async (e) => {
    e.preventDefault();
    if (city.trim()) {
      await fetchWeather(city.toLowerCase());
    }
  };

  const handleMarineSearch = async (e) => {
    e.preventDefault();
    if (latitude.trim() && longitude.trim()) {
      await fetchMarineWeather(latitude, longitude);
    }
  };

  return (
    <>
      <div className="hero-section" style={{backgroundImage: `url(${isMarineMode ? marineImage : heroImage})`}}>
        <div className="hero-tagline">
          " We bring you precise forecasts, so you never get caught unprepared by rain, shine, or storm."
        </div>
      </div>
      
      <div className="weather-container">
        <div className="mode-toggle">
          <button 
            onClick={() => setIsMarineMode(!isMarineMode)}
            className="mode-btn"
          >
            {isMarineMode ? 'Switch to Weather' : 'Switch to Marine Weather'}
          </button>
        </div>
      
        {!isMarineMode ? (
          <form onSubmit={handleCustomCity} className="custom-city">
            <input
              type="text"
              value={city}
              onChange={(e) => setCity(e.target.value)}
              placeholder="Enter city name"
              className="city-input"
            />
            <button type="submit" className="search-btn">Search</button>
          </form>
        ) : (
          <form onSubmit={handleMarineSearch} className="custom-city">
            <input
              type="number"
              value={latitude}
              onChange={(e) => setLatitude(e.target.value)}
              placeholder="Enter latitude"
              className="city-input"
              step="any"
            />
            <input
              type="number"
              value={longitude}
              onChange={(e) => setLongitude(e.target.value)}
              placeholder="Enter longitude"
              className="city-input"
              step="any"
            />
            <button type="submit" className="search-btn">Search Marine</button>
          </form>
        )}

      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error"> {error}</div>}
      
      {weatherData && (
        <div className="weather-section">
          <h1>{weatherData.city}</h1>
          
          <div className="weather-cards-container" style={{display: 'flex', gap: '15px', overflowX: 'auto', padding: '10px 0'}}>
            <div className="weather-card" style={{minWidth: '180px', flex: '0 0 auto'}}>
              <h3>Today</h3>
              <div className="temp">{weatherData.current?.temperature || weatherData.temperature}°C</div>
              <div className="condition">{weatherData.current?.condition || weatherData.condition}</div>
              <div className="humidity">Humidity: {weatherData.current?.humidity || weatherData.humidity}%</div>
            </div>

            {weatherData.daily && weatherData.daily.slice(0, 2).map((day, index) => (
              <div key={index} className="weather-card" style={{minWidth: '180px', flex: '0 0 auto'}}>
                <h3>{index === 0 ? 'Tomorrow' : `Day ${index + 1}`}</h3>
                <div className="temp">{day.temperature || day.temp?.day}°C</div>
                <div className="condition">{day.condition || day.weather?.[0]?.description}</div>
                <div className="humidity">Humidity: {day.humidity}%</div>
                <div className="date">{new Date(day.dt * 1000).toLocaleDateString()}</div>
              </div>
            ))}
          </div>
          
          <div className="timestamp">
            Updated: {new Date(weatherData.timestamp || Date.now()).toLocaleString()}
          </div>
        </div>
      )}

      {marineData && (
        <div className="weather-section">
          <h1>Marine Weather - Lat: {marineData.latitude}, Lng: {marineData.longitude}</h1>
          
          <div className="weather-cards-container" style={{display: 'flex', gap: '15px', overflowX: 'auto', padding: '10px 0'}}>
            <div className="weather-card" style={{minWidth: '180px', flex: '0 0 auto'}}>
              <h3>Today</h3>
              <div className="temp">Water: {marineData.waterTemperature}°C</div>
              <div className="condition">Wave: {marineData.waveHeight}m</div>
              <div className="humidity">Wind: {marineData.windSpeed} m/s</div>
              <div className="humidity">Direction: {marineData.windDirection}°</div>
            </div>

            {marineData.daily && marineData.daily.map((day, index) => (
              <div key={index} className="weather-card" style={{minWidth: '180px', flex: '0 0 auto'}}>
                <h3>{index === 0 ? 'Tomorrow' : `Day ${index + 1}`}</h3>
                <div className="temp">Water: {day.waterTemperature?.toFixed(1)}°C</div>
                <div className="condition">Wave: {day.waveHeight?.toFixed(1)}m</div>
                <div className="humidity">Wind: {day.windSpeed?.toFixed(1)} m/s</div>
                <div className="date">{new Date(day.dt * 1000).toLocaleDateString()}</div>
              </div>
            ))}
          </div>
          
          <div className="timestamp">
            Updated: {new Date(marineData.timestamp || Date.now()).toLocaleString()}
          </div>
        </div>
      )}
      {!isMarineMode && (
        <div className="city-buttons">
          {cities.map(cityName => (
            <button 
              key={cityName}
              onClick={() => fetchWeather(cityName)}
              className="city-btn"
            >
              {cityName.charAt(0).toUpperCase() + cityName.slice(1)}
            </button>
          ))}
        </div>
      )}

      </div>
    </>
  );
};

export default Home;
